import React, { useContext, useEffect, useState } from "react";
import "./Navigationbar_css.css";
import { Container, Nav, Navbar } from "react-bootstrap";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import travel from "./images/travel.png";
import { UserContext } from "./Auth/UserContext";

const Navigationbar = () => {
  const { userInfo, setUserInfo } = useContext(UserContext);
  const [loading, setLoading] = useState(true); // Loading state
  const nav = useNavigate();

  // Fetch logged-in admin info on mount
  useEffect(() => {
    const fetchUser = async () => {
      try {
        const res = await axios.get("http://localhost:5000/user/", {
          withCredentials: true,
        });
        setUserInfo(res.data);
      } catch (err) {
        setUserInfo({});
        console.log("Admin not logged in");
      } finally {
        setLoading(false);
      }
    };
    fetchUser();
  }, [setUserInfo]);

  const handleLogout = async () => {
    try {
      await axios.post(
        "http://localhost:5000/logout",
        null,
        { withCredentials: true }
      );
      setUserInfo({});
      nav("/login", { replace: true });
    } catch (err) {
      console.log("Logout failed");
    }
  };

  if (loading) return null; // prevent flicker

  return (
    <Navbar expand="lg" className="navbarStyle">
      <Container>
        <Navbar.Brand as={Link} to="/" style={{ color: "white", fontWeight: "bold" }}>
          <div className="imgStyle">
            Global Wings
            <img
              src={travel}
              alt="logo"
              style={{ height: "35px", width: "auto", objectFit: "contain", marginLeft: "8px" }}
            />
          </div>
        </Navbar.Brand>

        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            {/* Public Links */}
            <Nav.Link as={Link} to="/about" className="nav-link-custom">
              About us
            </Nav.Link>
            <Nav.Link as={Link} to="/packages" className="nav-link-custom">
              Travel tours
            </Nav.Link>
            <Nav.Link as={Link} to="/contact" className="nav-link-custom">
              Contact us
            </Nav.Link>

            {/* Admin Links */}
            {userInfo && userInfo.id && (
              <>
                <Nav.Link as={Link} to="/createPackage" className="nav-link-custom">
                  Create
                </Nav.Link>
                <Nav.Link as={Link} to="/readAll" className="nav-link-custom">
                  Read All
                </Nav.Link>
                <Nav.Link as={Link} to="/user" className="nav-link-custom">
                  Profile ({userInfo.username})
                </Nav.Link>
                <Nav.Link
                  onClick={handleLogout}
                  className="nav-link-custom"
                  style={{ cursor: "pointer" }}
                >
                  Logout
                </Nav.Link>
              </>
            )}

            {/* Login Link (only if admin not logged in) */}
            {!userInfo || !userInfo.id ? (
              <Nav.Link as={Link} to="/login" className="nav-link-custom">
                Login
              </Nav.Link>
            ) : null}
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default Navigationbar;
