import React, { useState, useEffect } from 'react'
import { Container, Card, Button, ListGroup, Image } from "react-bootstrap"
import { useParams, useNavigate } from 'react-router-dom'

const ReadOne2 = () => {
  const [item, setItem] = useState(null) // null instead of empty array
  const { id } = useParams()
  const nav = useNavigate()

  // Dummy data for frontend testing
  const dummyData = {
    _id: id || "1",
    packageName: "Albanian Adventure",
    packageDescription: "Explore the mountains and beaches of Albania.",
    packageDestination: "Tirana, Shkoder",
    packageDays: 5,
    packageTransportation: "private car",
    packagePrice: 600,
    packageImage: "albania.jpg",
    packageRegion: "Albania",
  }

  useEffect(() => {
    // Instead of fetching from backend, use dummy data
    setItem(dummyData)
  }, [id])

  const handleDelete = async (id) => {
    // For frontend testing, we can just log it
    console.log("Delete clicked for id:", id)
    nav("/") // simulate redirect after delete
  }

  if (!item) return <p>Loading...</p>

  return (
    <Container className="mt-5">
      <Card style={{ width: "25rem", margin: "0 auto" }}>
        <Image
          variant="top"
          src={`http://localhost:5000/images/${item.packageImage}`}
          style={{ height: "300px", objectFit: "cover" }}
        />

        <Card.Body>
          <Card.Title>{item.packageName}</Card.Title>
          <Card.Text>{item.packageDescription}</Card.Text>
        </Card.Body>

        <ListGroup className="list-group-flush">
          <ListGroup.Item>Destination: {item.packageDestination}</ListGroup.Item>
          <ListGroup.Item>Trip duration: {item.packageDays} days</ListGroup.Item>
          <ListGroup.Item>Transport: {item.packageTransportation}</ListGroup.Item>
          <ListGroup.Item>Price: {item.packagePrice} $</ListGroup.Item>
        </ListGroup>

        <Card.Body>
          <Button variant="warning" href={`/updateItem/${item._id}`} className="me-2">
            Update
          </Button>
          <Button variant='danger' onClick={() => handleDelete(item._id)}>Delete</Button>
        </Card.Body>
      </Card>
    </Container>
  )
}

export default ReadOne2;
