import React, { useState, useEffect } from "react";
import axios from "axios";
import { Container, Row, Col } from "react-bootstrap";
import { useLocation } from "react-router-dom";
import PackageInfo from "./PackageInfo";

const ReadTours = () => {
  const [items, setItems] = useState([]);
  const location = useLocation();

  // Read query param (?packageRegion=Europe)
  const region = new URLSearchParams(location.search).get("packageRegion");

  useEffect(() => {
    const fetchPackages = async () => {
      try {
        const url = region
          ? `http://localhost:5000/readByRegion/${region}`
          : `http://localhost:5000/readAll/`;

        const res = await axios.get(url);
        setItems(Array.isArray(res.data) ? res.data : []);
      } catch (err) {
        console.log("Items not read:", err);
        setItems([]);
      }
    };

    fetchPackages();
  }, [region]);

  return (
    <Container className="mt-5">
      <h2 className="mb-4 text-center">
        {region ? `${region} Tours` : "All Tours"}
      </h2>

      {items.length === 0 ? (
        <p className="text-center fs-5 mt-4">
          No travel tours to show.
        </p>
      ) : (
        <Row className="justify-content-center">
          {items.map((item) => (
            <Col
              key={item._id}
              className="mb-5 d-flex justify-content-center"
            >
              <PackageInfo {...item} />
            </Col>
          ))}
        </Row>
      )}
    </Container>
  );
};

export default ReadTours;
