import React, {useState, useEffect } from "react";
import {Container, Form, Button, Row, Col, Image} from "react-bootstrap";
import axios from "axios";
import {useNavigate } from "react-router-dom";


const CreatePackage = () => {
// Metode navigimi (nga njeri komponent tek tjetri)
const navigate = useNavigate();
// Cdo input ka nje state ku do te ruhen informacionet
const [newItem, setNewItem] = useState({
    packageName: "",
    packageDescription: "",
    packageRegion: "",
    packageDestination: "",
    packageDays: "",
    packageTransportation: "",
    packagePrice: "",
    packageImages: "",
});

// State i afishimit te imazhit
const [uploadedImage, setUploadedImage] = useState(null);
// Marrja e informacioni nga input-et: text, number, radio, textarea, select etj
const handleChange = (e) => {
setNewItem({ ...newItem, [e.target.name]: e.target.value })
}
// Marrja e informacionit nga input me type file
const handlePhoto = (e) => {
setNewItem({ ...newItem, photo: e.target.files[0] })
setUploadedImage(URL.createObjectURL(e.target.files[0]));
}

// Funksioni qe do te perdoret per te kaluar te dhenat ne backend
const handleSubmit = async (e) => {
e.preventDefault();
// Te dhenat do te kalojne tek metoda Form data
const formData = new FormData();
// Marrja e informacionit nga input-et,
// infomacioni merret nga atributi name, i cili kalon tek key
Object.entries(newItem).forEach(([key, value]) => {
formData.append(key, value);
});
// Therritja e app => Create
await axios.post("http://localhost:5000/create", formData)
.then((res) => {
// Testimi
console.log(res.data);
// Pas krijimit kallohet tek faqja kryesore
navigate("/readAll");
})
// Nese nuk krijohet elementi i ri
.catch((err) => {
console.log("Error server, Item not created" + err);
});
};

return (
<Container className="my-5">
{/* Nese user-i eshte i loguar shfaqet form-a dhe nese jo shfaqet nje mesazh */}
<Row>
<Col>
<h1>Create</h1>
{/* Therritja e funksionit dhe encType lejon marrjen e file/imazheve */}
{/* Cdo input duhet te kete atributin name, value dhe eventin onChange */}
<Form onSubmit={handleSubmit} encType='multipart/form-data'>

<Form.Group className="mb-3" controlId="name">
<Form.Label>Name of tour package</Form.Label>
<Form.Control
type="text"
name="name"
value={newItem.name}
onChange={handleChange}
/>
</Form.Group>

<Form.Group className="mb-3" controlId="description">
<Form.Label>Description</Form.Label>
<Form.Control
value={newItem.packageDescription}
onChange={handleChange}
as="textarea" name="packageDescription" rows={3}/>
</Form.Group>

<Form.Group className="mb-3" controlId="region">
<Form.Select
    value={newItem.packageRegion}
    onChange={handleChange}
    name="packageRegion"  
    required 
  >
    <option value="">-- Select a region --</option>
    <option value="Albania">Albania</option>
    <option value="Europe">Europe</option>
    <option value="Middle East">Middle East</option>
    <option value="Far East">Far East</option>
  </Form.Select>
</Form.Group>

<Form.Group className="mb-3" controlId="destination">
<Form.Label>Desctination</Form.Label>
<Form.Control
type="text"
name="packageDestination"
value={newItem.packageDestination}
onChange={handleChange}
/>
</Form.Group>

<Form.Group className="mb-3" controlId="days">
<Form.Label>Days of stay</Form.Label>
<Form.Control
type="number"
name="packageDays"
value={newItem.packageDays}
onChange={handleChange}
/>
</Form.Group>

<Form.Group className="mb-3" controlId="transportation">
<Form.Select
    value={newItem.packageTransportation}
    onChange={handleChange}
    name="packageTransportation"  
    required 
  ><option value="">-- Select an option --</option>
    <option value="Airplane">Airplane</option>
    <option value="Train">Train</option>
    <option value="Private car">Private car</option>
    <option value="Cruise">Cruise</option>
  </Form.Select>
</Form.Group>

<Form.Group className="mb-3" controlId="price">
<Form.Label>Price</Form.Label>
<Form.Control
type="number"
name="packagePrice"
value={newItem.packagePrice}
onChange={handleChange}
/>
</Form.Group>


<Form.Group className="mb-3" controlId="Image">
<Form.Label>Image</Form.Label>
<Form.Control
type="file"
// Nuk duhet te kete atributin value
// Lejohen vetem keto tipe file-sh
accept=".jpeg, .png, .jpg"
onChange={handlePhoto}
name="packageImage"/>
</Form.Group>


<Button variant="primary" type="submit" value="Create">
Submit
</Button>
</Form>
</Col>
<div>
<h1>Preview Image</h1>
{/* Shfaqja e imazhit */}
{uploadedImage && (
<Image
src={uploadedImage}
alt='Uploaded'
rounded
className='img-fluid'
/>
)}
</div>
</Row>
</Container>
);

}

export default CreatePackage;
