import React from "react";
import { Card, Button, Badge } from "react-bootstrap";
import { useNavigate } from "react-router-dom";
import "./PackageInfo.css"; 

const PackageInfo = ({
  packageImage,
  packageName,
  packageDescription,
  packageDestination,
  packageDays,
  packageTransportation,
  packagePrice,
  packageRegion,
}) => {
  const navigate = useNavigate();

  const handleBooking = () => {
    navigate("/reservation", {
      state: { packageName, packageRegion },
    });
  };

  return (
    <Card className="package-card">
      <div className="package-img-container">
        <img
          src={`http://localhost:5000/images/${packageImage}`}
          alt={packageName}
          className="package-img"
        />
        <Badge className="badge-left">{packageRegion}</Badge>
        <Badge className="badge-right">{packageDestination}</Badge>
      </div>

      <Card.Body>
        <Card.Title className="package-title">{packageName}</Card.Title>
        <Card.Text className="package-desc">{packageDescription}</Card.Text>

        <div className="package-info d-flex justify-content-between mb-3">
          <small><strong>Duration:</strong> {packageDays} days</small>
          <small><strong>Transport:</strong> {packageTransportation}</small>
        </div>

        <div className="d-flex justify-content-between align-items-center">
          <h5 className="package-price">{packagePrice} $</h5>
          <Button variant="primary" onClick={handleBooking}>
            Book Now
          </Button>
        </div>
      </Card.Body>
    </Card>
  );
};

export default PackageInfo;
