import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import axios from "axios";
import { useLocation } from "react-router-dom"; // Added for hidden data
import "./Contact.css"; // Keep your styling
import together from "./images/together.png";

const Reservation = () => {
  const location = useLocation();
  // Get the package info passed from the "Book Now" button
  const { packageName, packageRegion } = location.state || {};

  const [booking, setBooking] = useState({
    Name: "",
    Surname: "",
    phone: "",
    email: "",
    comment: "",
    // These are filled automatically from the state
    packageName: packageName || "",
    packageRegion: packageRegion || "",
  });

  const [msg, setMsg] = useState("");
  const [submitted, setSubmitted] = useState(false);

  // ---------------- VALIDATION ----------------
  const checkBooking = () => {
    if (
      booking.Name.trim() === "" ||
      booking.Surname.trim() === "" ||
      booking.phone.trim() === "" ||
      booking.email.trim() === ""
    ) {
      setMsg("Please fill in all the required fields!");
      return false;
    }

    setMsg("Reservation submitted successfully! Our team will contact you.");
    return true;
  };

  // ---------------- INPUT HANDLER ----------------
  const handleChange = (e) => {
    const { name, value } = e.target;
    setBooking({ ...booking, [name]: value });
  };

  // ---------------- SUBMIT ----------------
  const handleSubmit = async (e) => {
    e.preventDefault();
    setSubmitted(true);

    if (!checkBooking()) return;

    try {
      // Updated to your new backend route
      await axios.post("http://localhost:5000/reservation", booking);
      console.log("Reservation saved");

      // Reset form (keeping package context if they want to book again)
      setBooking({
        Name: "",
        Surname: "",
        phone: "",
        email: "",
        comment: "",
        packageName: packageName || "",
        packageRegion: packageRegion || "",
      });
    } catch (err) {
      console.error("Error saving reservation:", err);
      setMsg("Something went wrong. Please try again.");
    }
  };

  const decideAlert = () => {
    return msg.includes("Please fill") ? "danger" : "success";
  };

  return (
    <Container className="helper-container mt-5">
      <h4 style={{ fontWeight: "bold" }}>
        Complete your reservation for: {packageName || "Selected Tour"}
      </h4>
      <h6>
        Region: {packageRegion || "Not specified"}
      </h6>

      <img src={together} className="imgStyle" alt="Travel" />

      {/* ---------------- FORM ---------------- */}
      <Form className="helper-form" onSubmit={handleSubmit}>
        <Form.Group className="mb-3">
          <Form.Label>First Name</Form.Label>
          <Form.Control
            type="text"
            placeholder="John"
            name="Name"
            value={booking.Name}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Surname</Form.Label>
          <Form.Control
            type="text"
            placeholder="Smith"
            name="Surname"
            value={booking.Surname}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Phone number</Form.Label>
          <Form.Control
            type="text"
            placeholder="(+355) 68..."
            name="phone"
            value={booking.phone}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Email address</Form.Label>
          <Form.Control
            type="email"
            placeholder="name@example.com"
            name="email"
            value={booking.email}
            onChange={handleChange}
          />
        </Form.Group>

        <Form.Group className="mb-3">
          <Form.Label>Special Requests / Comments</Form.Label>
          <Form.Control
            as="textarea"
            rows={3}
            name="comment"
            value={booking.comment}
            onChange={handleChange}
          />
        </Form.Group>

        <Button variant="success" type="submit" size="lg">
          Confirm Booking
        </Button>
      </Form>

      {/* ---------------- ALERT ---------------- */}
      {submitted && (
        <Alert className="mt-3" variant={decideAlert()}>
          {msg}
        </Alert>
      )}
    </Container>
  );
};

export default Reservation;
